import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { PetService } from '../services/pet.services';
import { PetBreedService } from '../services/petBreed.service';
import { PetOwnerService } from '../services/petowner.service';
import { PetRequestModel } from '../models/request.models';

@Component({
  selector: 'app-add-pet',
  templateUrl: './add-pet.component.html',
  styleUrls: ['./add-pet.component.css']
})
export class AddPetComponent implements OnInit {
  newForm: FormGroup;
  petTypes: any[] = []; // Assuming PetTypeResponseModel has an 'id' and 'name' property
  petBreeds: any[] = []; // Assuming PetBreedResponseModel has an 'id' and 'name' property
  owners: any[] = []; // Assuming PetOwnerResponseModel has an 'id' and 'name' property

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private petTypeService: PetService,
    private petBreedService: PetBreedService,
    private petOwnerService: PetOwnerService
  ) {
    this.newForm = this.fb.group({
      name: ['', Validators.required],
      petTypeId: [null, Validators.required],
      petBreedId: [null, Validators.required],
      ownerId: [null, Validators.required]
    });
  }

  ngOnInit(): void {
    this.fetchPetTypes();
    this.fetchPetBreeds();
    this.fetchOwners();
  }

  fetchPetTypes(): void {
    this.petTypeService.getAllPets().subscribe(types => {
      this.petTypes = types;
    });
  }

  fetchPetBreeds(): void {
    this.petBreedService.getAllPetBreeds().subscribe(breeds => {
      this.petBreeds = breeds;
    });
  }

  fetchOwners(): void {
    this.petOwnerService.getAllPetOwners().subscribe(owners => {
      this.owners = owners;
    });
  }

  onSubmit(): void {
    if (this.newForm.valid) {
      const petData: PetRequestModel = {
        name: this.newForm.value.name,
        petTypeId: this.newForm.value.petTypeId,
        petBreedId: this.newForm.value.petBreedId,
        petOwnerId: this.newForm.value.ownerId,
        id: 0
      };

      // Call your service method to add the pet
      // Example: this.petService.addPet(petData).subscribe(response => { ... });

      this.router.navigate(['/pets']); // Navigate to pets list after successful submission
    } else {
      // Mark all fields as touched to display validation errors
      Object.keys(this.newForm.controls).forEach(field => {
        const control = this.newForm.get(field);
        control?.markAsTouched({ onlySelf: true });
      });
    }
  }
}
