import { Component } from '@angular/core';

@Component({
  selector: 'app-edit-breed',
  templateUrl: './edit-breed.component.html',
  styleUrls: ['./edit-breed.component.css']
})
export class EditBreedComponent implements OnInit {
  editForm: FormGroup;
  breedId: number;
  breeds: any[] = [
    { id: 1, name: 'Golden Retriever' },
    { id: 2, name: 'Labrador Retriever' },
    { id: 3, name: 'German Shepherd' },
  ];

  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    private router: Router
  ) {
    this.editForm = this.fb.group({
      name: ['', Validators.required]
    });
    this.breedId = +this.route.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    const breed = this.breeds.find(b => b.id === this.breedId);
    if (breed) {
      this.editForm.patchValue(breed);
    }
  }

  onSubmit(): void {
    if (this.editForm.valid) {
      const index = this.breeds.findIndex(b => b.id === this.breedId);
      if (index !== -1) {
        this.breeds[index].name = this.editForm.value.name;
        this.router.navigate(['/breeds']);
      }
    }
  }
}
