import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-edit-pet',
  templateUrl: './edit-pet.component.html',
  styleUrls: ['./edit-pet.component.css']
})
export class EditPetComponent  implements OnInit {
  editForm: FormGroup;
  petId: number=-1;
  owners = [
    { id: 1, name: 'John Doe' },
    { id: 2, name: 'Jane Smith' }
  ];
  pets = [
    { id: 1, name: 'Buddy', petType: 'Dog', petBreed: 'Labrador', ownerId: 1 },
    { id: 2, name: 'Mittens', petType: 'Cat', petBreed: 'Siamese', ownerId: 2 }
  ];

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.editForm = this.formBuilder.group({
      name: ['', Validators.required],
      petType: ['', Validators.required],
      petBreed: ['', Validators.required],
      ownerId: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.loadPetDetails();
  }

  loadPetDetails(): void {
    const pet = this.pets.find(p => p.id === this.petId);
    if (pet) {
      this.editForm.patchValue({
        name: pet.name,
        petType: pet.petType,
        petBreed: pet.petBreed,
        ownerId: pet.ownerId
      });
    } else {
      console.error('Pet not found');
    }
  }

  onSubmit(): void {
    if (this.editForm.valid) {
      // Normally here you would call a service to save the pet details
      console.log('Updated pet details:', this.editForm.value);
      this.router.navigate(['/pets']);
    } 
    }


  get form() { return this.editForm.controls; }
}
