import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-breed',
  templateUrl: './add-breed.component.html',
  styleUrls: ['./add-breed.component.css']
})
export class AddBreedComponent implements OnInit {
  newForm: FormGroup;
  breeds: any[] = [
    { id: 1, name: 'Golden Retriever' },
    { id: 2, name: 'Labrador Retriever' },
    { id: 3, name: 'German Shepherd' },
  ];

  constructor(
    private fb: FormBuilder,
    private router: Router
  ) {
    this.newForm = this.fb.group({
      name: ['', Validators.required]
    });
  }

  ngOnInit(): void { }

  onSubmit(): void {
    if (this.newForm.valid) {
      const newId = Math.max(...this.breeds.map(b => b.id)) + 1;
      this.breeds.push({ id: newId, name: this.newForm.value.name });
      this.router.navigate(['/breeds']);
    }
  }
}
