import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-appointments',
  templateUrl: './appointments.component.html',
  styleUrls: ['./appointments.component.css']
})
export class AppointmentsComponent {
  appointments: any[] = [
    { id: 1, petName: 'Max', ownerName: 'John Doe', date: new Date('2024-07-05'), reason: 'Checkup' },
    { id: 2, petName: 'Bella', ownerName: 'Jane Smith', date: new Date('2024-07-10'), reason: 'Vaccination' }
    // Add more dummy data as needed
  ];

  filteredAppointments!: any[];
  searchTerm: string = '';

  constructor(private router: Router) { }

  ngOnInit(): void {
    this.filteredAppointments = [...this.appointments]; // Initialize filteredAppointments with all appointments
  }

  deleteAppointment(id: number): void {
    // Implement delete functionality
    console.log('Deleting appointment with ID:', id);
    // Example: Remove appointment from the list
    this.appointments = this.appointments.filter(appointment => appointment.id !== id);
    this.filterAppointments();
  }

  filterAppointments(): void {
    if (this.searchTerm.trim() !== '') {
      this.filteredAppointments = this.appointments.filter(appointment =>
        appointment.petName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        appointment.ownerName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        appointment.reason.toLowerCase().includes(this.searchTerm.toLowerCase())
      );
    } else {
      this.filteredAppointments = [...this.appointments]; // Reset filteredAppointments to all appointments
    }
  }


}
<div>
    <div class="row mb-3">
        <div class="col-md-6">
            <h2>Upcoming Appointments</h2>
        </div>
    </div>

    <!-- Filter and Buttons Section -->
    <div class="row mb-3">
        <div class="col-md-6">
            <input type="text" class="form-control" placeholder="Search appointments..." [(ngModel)]="searchTerm" (ngModelChange)="filterAppointments()">
        </div>
        <div class="col-md-6 text-right">
            <div class="btn-group" role="group">
                <a routerLink="/appointments/new" class="btn btn-primary">New Appointment</a>
                <a routerLink="/owners" class="btn btn-primary ml-2">Owners</a>
                <a routerLink="/pets" class="btn btn-primary ml-2">Pets</a>
                <a routerLink="/pet-types-list" class="btn btn-primary ml-2">Pet Types</a>
                <a routerLink="/breed-list" class="btn btn-primary ml-2">Pet Breed</a>
            </div>
        </div>
    </div>

    <!-- Appointments Table -->
    <div class="table-responsive">
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Pet's Name</th>
                    <th>Owner's Name</th>
                    <th>Date</th>
                    <th>Reason</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let appointment of filteredAppointments">
                    <td>{{ appointment.petName }}</td>
                    <td>{{ appointment.ownerName }}</td>
                    <td>{{ appointment.date | date }}</td>
                    <td>{{ appointment.reason }}</td>
                    <td>
                        <div class="btn-group" role="group" aria-label="Appointment Actions">
                            <a routerLink="/appointments/{{ appointment.id }}/edit" class="btn btn-sm btn-primary">Edit</a>
                            <button class="btn btn-sm btn-danger ml-2" (click)="deleteAppointment(appointment.id)">Delete</button>
                        </div>
                    </td>
                </tr>
                <tr *ngIf="filteredAppointments.length === 0">
                    <td colspan="5" class="text-center">No appointments found.</td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
