// owner-list.component.ts

import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { PetOwnerService } from '../services/petowner.service';
import { PetBreedResponseModel, PetOwnerResponseModel } from '../models/response.models';
@Component({
  selector: 'app-owner-list',
  templateUrl: './owner-list.component.html',
  styleUrls: ['./owner-list.component.css']
})
export class OwnerListComponent implements OnInit {
  owners: PetOwnerResponseModel[] = []; // Assuming Owner model has properties like name, address, phoneNumber, emailAddress
  filteredOwners: PetOwnerResponseModel[] = [];
  searchTerm: string = '';

  constructor(private router: Router, private ownerService: PetOwnerService) {}

  ngOnInit(): void {
    this.fetchOwners();
  }

  fetchOwners(): void {
    this.ownerService.getAllPetOwners().subscribe({
      next: (owners: any[]) => {
        this.owners = owners;
        this.filteredOwners = [...this.owners]; // Initialize filteredOwners with all owners initially
      },
      error: (error) => {
        console.error('Error fetching owners:', error);
        // Handle error scenario, show user-friendly message, etc.
      }
    });
  }

  filterOwners(): void {
    // Filter owners based on searchTerm
    const search = this.searchTerm.toLowerCase();``
    this.filteredOwners = this.owners.filter(owner =>
      owner.name.toLowerCase().includes(search) ||
      owner.address.line1.toLowerCase().includes(search) ||
      owner.address.city.toLowerCase().includes(search) ||
      owner.phoneNumber.includes(search) ||
      owner.emailAddress.toLowerCase().includes(search)
    );
  }

  clearSearch(): void {
    this.searchTerm = '';
    this.filterOwners();
  }

  goToNewOwner(): void {
    this.router.navigate(['/owners/new']); // Assuming you have a route for creating a new owner
  }

  editOwner(id: number): void {
    this.router.navigate([`/owners/${id}/edit`]); // Assuming you have a route for editing an owner
  }

  deleteOwner(id: number): void {
    // Implement deletion logic here (e.g., call a service to delete the owner)
    console.log(`Deleted owner with ID ${id}`);
    // After deletion, fetch owners again or update the list as needed
  }
}
