import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { AppointmentService } from '../services/Address.services';

@Component({
  selector: 'app-appointments',
  templateUrl: './appointments.component.html',
  styleUrls: ['./appointments.component.css']
})
export class AppointmentsComponent {
  appointments: any[] = [];

  filteredAppointments!: any[];
  searchTerm: string = '';

  constructor(
    private router: Router, 
    private appointmentServices : AppointmentService

   ) { }

  ngOnInit(): void {
    this.getAllAppointment();
  }


  getAllAppointment(){
    this.appointmentServices.getAllAppointments().subscribe( res => {
       this.appointments = res;
       this.filteredAppointments = [...this.appointments]; // Initialize filteredAppointments with all appointments
    })
  }
  deleteAppointment(id: number): void {
    // Implement delete functionality
    console.log('Deleting appointment with ID:', id);
    // Example: Remove appointment from the list
    this.appointments = this.appointments.filter(appointment => appointment.id !== id);
    this.filterAppointments();
  }

  filterAppointments(): void {
    if (this.searchTerm.trim() !== '') {
      this.filteredAppointments = this.appointments.filter(appointment =>
        appointment.petName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        appointment.ownerName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        appointment.reason.toLowerCase().includes(this.searchTerm.toLowerCase())
      );
    } else {
      this.filteredAppointments = [...this.appointments]; // Reset filteredAppointments to all appointments
    }
  }


}
