import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-breed-list',
  templateUrl: './breed-list.component.html',
  styleUrls: ['./breed-list.component.css']
})
export class BreedListComponent  implements OnInit {
  breeds: any[] = [
    { id: 1, name: 'Golden Retriever' },
    { id: 2, name: 'Labrador Retriever' },
    { id: 3, name: 'German Shepherd' },
  ];
  filteredAppointments!: any[];
  searchTerm: string = '';
  filteredBreeds: any[] = this.breeds;



  constructor() { }

  ngOnInit(): void {
    this.filterBreeds();
  }
  filterBreeds(): void {
    const lowerCaseTerm = this.searchTerm.toLowerCase();
    this.filteredBreeds = this.breeds.filter(breed =>
      breed.name.toLowerCase().includes(lowerCaseTerm)
    );
  }
  deleteBreed(id: number): void {
    if (confirm('Are you sure you want to delete this breed?')) {
      this.breeds = this.breeds.filter(b => b.id !== id);
    }
  }


}
