import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-appointment',
  templateUrl: './new-appointment.component.html',
  styleUrls: ['./new-appointment.component.css']
})
export class NewAppointmentComponent implements OnInit {
  newForm!: FormGroup;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.buildForm();
  }

  buildForm(): void {
    this.newForm = this.formBuilder.group({
      petName: ['', Validators.required],
      ownerName: ['', Validators.required],
      date: ['', Validators.required],
      reason: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.newForm.valid) {
      // Handle form submission logic here
      console.log('Form submitted successfully!', this.newForm.value);
    } else {
      // Mark all form fields as touched to trigger validation messages
      this.markFormGroupTouched(this.newForm);
    }
  }

  markFormGroupTouched(formGroup: FormGroup): void {
    Object.values(formGroup.controls).forEach(control => {
      control.markAsTouched();
      if (control instanceof FormGroup) {
        this.markFormGroupTouched(control);
      }
    });
  }

  get petName() {
    return this.newForm.get('petName');
  }

  get ownerName() {
    return this.newForm.get('ownerName');
  }

  get date() {
    return this.newForm.get('date');
  }

  get reason() {
    return this.newForm.get('reason');
  }
}
