import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AppointmentService } from '../services/appointment.service';

@Component({
  selector: 'app-new-appointment',
  templateUrl: './new-appointment.component.html',
  styleUrls: ['./new-appointment.component.css']
})
export class NewAppointmentComponent implements OnInit {
  newForm!: FormGroup ;
  owners: any[] = []; // Assuming you have a list of owners with 'id' and 'name'
  pets: any[] = []; // Assuming you have a list of pets with 'id', 'name', and 'ownerId'

  constructor(private formBuilder: FormBuilder, private appointmentServices : AppointmentService  ) {
   }

  ngOnInit(): void {
    this.buildForm();
  }

  buildForm(): void {
    this.newForm = this.formBuilder.group({
      petName: ['', Validators.required],
      ownerName: ['', Validators.required],
      date: ['', Validators.required],
      reason: ['', Validators.required]
    });
  }

  onChangeOwner() {
    const ownerId = this.newForm.get('ownerId')?.value;
    if (ownerId) {
      this.pets = this.pets.filter(pet => pet.ownerId === ownerId);
    } else {
      // Handle the case where ownerId is null or undefined if needed
      this.pets = []; // Reset pets list or handle differently
    }
  }



  onSubmit(): void {
    if (this.newForm.valid) {
      // Call createAppointment method from AppointmentService
      this.appointmentServices.createAppointment(this.newForm.value).subscribe(
        (response: boolean) => {
          console.log('Appointment created successfully!', response);
          this.newForm.reset();
        },
        (error: any) => {
          console.error('Error creating appointment:', error);
        }
      );
    } else {
      // Mark all form fields as touched to trigger validation messages
      this.markFormGroupTouched(this.newForm);
    }
  }

  markFormGroupTouched(formGroup: FormGroup): void {
    Object.values(formGroup.controls).forEach(control => {
      control.markAsTouched();
      if (control instanceof FormGroup) {
        this.markFormGroupTouched(control);
      }
    });
  }

  // Getter methods to simplify access to form controls in the template
  get petName() {
    return this.newForm.get('petName');
  }

  get ownerName() {
    return this.newForm.get('ownerName');
  }

  get date() {
    return this.newForm.get('date');
  }

  get reason() {
    return this.newForm.get('reason');
  }
}
